// Example to filter sensitive data to prevent cross-site scripting
public Class Validator {
    ...
    public static String filter(String value) {
        if (value == null) {
            return null;
        }        
        StringBuffer result = new StringBuffer(value.length());
        for (int i=0; i<value.length(); ++i) {
            switch (value.charAt(i)) {
            case '<':
                result.append("&lt;");
                break;
            case '>': 
                result.append("&gt;");
                break;
            case '"': 
                result.append("&quot;");
                break;
            case '\'': 
                result.append("&#39;");
                break;
            case '%': 
                result.append("&#37;");
                break;
            case ';': 
                result.append("&#59;");
                break;
            case '(': 
                result.append("&#40;");
                break;
            case ')': 
                result.append("&#41;");
                break;
            case '&': 
                result.append("&amp;");
                break;
            case '+':
                result.append("&#43;");
                break;
            default:
                result.append(value.charAt(i));
                break;
        }        
        return result;
    }
    ...
}
